
public class nestedloops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=1;i<=4;i++)
		{
			//System.out.println("outer loop started");
			for(int j=1;j<=4;j++)
			{
				System.out.println("this is inner loop");
			}
		//	System.out.println("outer loop ended");
			}
				
		}
	}


